Fn::Sub:
{
  "StartAt": "Enrich Input Loan Creation",
  "States": {
    "Enrich Input Loan Creation": {
      "Type": "Task",
      "Resource": "arn:aws:states:::sqs:sendMessage.waitForTaskToken",
      "Parameters": {
        "QueueUrl": "${5d76f7b2e6b758b99bc5afdaebb10094}",
        "MessageBody": {
          "TaskToken.$": "$$.Task.Token",
          "Input.$": "$"
        },
        "MessageAttributes": {
          "contentType": {
            "DataType": "String",
            "StringValue": "application/json"
          }
        }
      },
      "Next": "Create Loan Account",
      "Retry": [
        {
          "ErrorEquals": [
            "RETRYABLE_ERROR"
          ],
          "IntervalSeconds": 3,
          "BackoffRate": 2,
          "MaxAttempts": 5,
          "MaxDelaySeconds": 2
        }
      ],
      "Catch": [
        {
          "ErrorEquals": [
            "States.ALL"
          ],
          "ResultPath": "$.errorOutput",
          "Next": "Notify Loan Creation Failure"
        }
      ]
    },
    "Create Loan Account": {
      "Type": "Task",
      "Resource": "arn:aws:states:::sqs:sendMessage.waitForTaskToken",
      "Parameters": {
        "QueueUrl": "${8d7d74974fc263dbc7ea746a69a656d2}",
        "MessageBody": {
          "TaskToken.$": "$$.Task.Token",
          "Input.$": "$"
        },
        "MessageAttributes": {
          "contentType": {
            "DataType": "String",
            "StringValue": "application/json"
          }
        }
      },
      "Next": "Approve Loan Account",
      "Retry": [
        {
          "ErrorEquals": [
            "RETRYABLE_ERROR"
          ],
          "IntervalSeconds": 3,
          "BackoffRate": 2,
          "MaxAttempts": 5,
          "MaxDelaySeconds": 2
        }
      ],
      "Catch": [
        {
          "ErrorEquals": [
            "PROFILE_NOT_ELIGIBLE"
          ],
          "ResultPath": "$.errorOutput",
          "Next": "Notify Profile Not Eligible For Creation"
        },
        {
          "ErrorEquals": [
            "States.ALL"
          ],
          "ResultPath": "$.errorOutput",
          "Next": "Notify Loan Creation Failure"
        }
      ]
    },
    "Notify Profile Not Eligible For Creation": {
      "Type": "Task",
      "Resource": "arn:aws:states:::sqs:sendMessage.waitForTaskToken",
      "Parameters": {
        "QueueUrl": "${d638b87b48a53cd9c01f43d8c756ade2}",
        "MessageBody": {
          "TaskToken.$": "$$.Task.Token",
          "Input.$": "$"
        },
        "MessageAttributes": {
          "contentType": {
            "DataType": "String",
            "StringValue": "application/json"
          },
          "messageTemplateId": {
            "DataType": "String",
            "StringValue": "001"
          }
        }
      },
      "End": true
    },
    "Approve Loan Account": {
      "Type": "Task",
      "Resource": "arn:aws:states:::sqs:sendMessage.waitForTaskToken",
      "Parameters": {
        "QueueUrl": "${350bb5aae5f02313eb0720836cdcb6ff}",
        "MessageBody": {
          "TaskToken.$": "$$.Task.Token",
          "Input.$": "$"
        },
        "MessageAttributes": {
          "contentType": {
            "DataType": "String",
            "StringValue": "application/json"
          }
        }
      },
      "End": true,
      "Retry": [
        {
          "ErrorEquals": [
            "RETRYABLE_ERROR"
          ],
          "IntervalSeconds": 3,
          "BackoffRate": 2,
          "MaxAttempts": 5,
          "MaxDelaySeconds": 2
        }
      ],
      "Catch": [
        {
          "ErrorEquals": [
            "PROFILE_NOT_ELIGIBLE"
          ],
          "ResultPath": "$.errorOutput",
          "Next": "Can Notify For The First Time?"
        },
        {
          "ErrorEquals": [
            "States.ALL"
          ],
          "Next": "Notify Loan Creation Failure",
          "ResultPath": "$.errorOutput"
        }
      ]
    },
    "Can Notify For The First Time?": {
      "Type": "Choice",
      "Choices": [
        {
          "Variable": "$.retryCount",
          "NumericEquals": 1,
          "Next": "Notify Profile Not Eligible For First Time Approval"
        }
      ],
      "Default": "Calculate Next Interval Time"
    },
    "Notify Profile Not Eligible For First Time Approval": {
      "Type": "Task",
      "Resource": "arn:aws:states:::sqs:sendMessage.waitForTaskToken",
      "Parameters": {
        "QueueUrl": "${d638b87b48a53cd9c01f43d8c756ade2}",
        "MessageBody": {
          "TaskToken.$": "$$.Task.Token",
          "Input.$": "$"
        },
        "MessageAttributes": {
          "messageTemplateId": {
            "DataType": "String",
            "StringValue": "002"
          }
        }
      },
      "Next": "Increase Notification Count"
    },
    "Increase Notification Count": {
      "Type": "Pass",
      "Parameters": {
        "original.$": "$",
        "new": {
          "retryCount.$": "States.MathAdd($.retryCount, 1)"
        }
      },
      "Next": "Merge Notification Input"
    },
    "Merge Notification Input": {
      "Type": "Pass",
      "Parameters": {
        "merged.$": "States.JsonMerge($.original, $.new, false)"
      },
      "OutputPath": "$.merged",
      "Next": "Calculate Next Interval Time"
    },
    "Calculate Next Interval Time": {
      "Type": "Task",
      "Resource": "arn:aws:states:::sqs:sendMessage.waitForTaskToken",
      "Parameters": {
        "QueueUrl": "${5468daa7e6a5d7af195878fedd31a858}",
        "MessageBody": {
          "TaskToken.$": "$$.Task.Token",
          "Input.$": "$"
        },
        "MessageAttributes": {
          "contentType": {
            "DataType": "String",
            "StringValue": "application/json"
          }
        }
      },
      "Next": "Schedule For Retry Approval",
      "Catch": [
        {
          "ErrorEquals": [
            "PROFILE_NOT_ELIGIBLE"
          ],
          "ResultPath": "$.errorOutput",
          "Next": "Notify Profile Not Eligible For Last Time Approval"
        },
        {
          "ErrorEquals": [
            "States.ALL"
          ],
          "Next": "Notify Loan Creation Failure",
          "ResultPath": "$.errorOutput"
        }
      ]
    },
    "Schedule For Retry Approval": {
      "Type": "Wait",
      "TimestampPath": "$.nextInterval",
      "Next": "Approve Loan Account"
    },
    "Notify Profile Not Eligible For Last Time Approval": {
      "Type": "Task",
      "Resource": "arn:aws:states:::sqs:sendMessage.waitForTaskToken",
      "Parameters": {
        "QueueUrl": "${d638b87b48a53cd9c01f43d8c756ade2}",
        "MessageBody": {
          "TaskToken.$": "$$.Task.Token",
          "Input.$": "$"
        },
        "MessageAttributes": {
          "contentType": {
            "DataType": "String",
            "StringValue": "application/json"
          },
          "messageTemplateId": {
            "DataType": "String",
            "StringValue": "003"
          }
        }
      },
      "End": true
    },
    "Notify Loan Creation Failure": {
      "Type": "Task",
      "Resource": "arn:aws:states:::sqs:sendMessage.waitForTaskToken",
      "Parameters": {
        "QueueUrl": "${c265ac0d4596c9a9f0faa6bb53cd3009}",
        "MessageBody": {
          "TaskToken.$": "$$.Task.Token",
          "Input.$": "$"
        },
        "MessageAttributes": {
          "contentType": {
            "DataType": "String",
            "StringValue": "application/json"
          }
        }
      },
      "End": true
    }
  }
}
